package Day1;

public class Addsubmuldiv {
	
	static int a=100;
	static int b=50;
	static void add()
	{
		int c=a+b;
		System.out.println(c);
	}
	static void sub()
	{
		int d=a-b;
		System.out.println(d);
	}
	static void mul()
	{
		int e=a*b;
		System.out.println(e);
	}
	static void div()
	{
		int f=a/b;
		System.out.println(f);
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		add();
		sub();
		mul();
		div();

	}

}
